            time[i][j] = v[i]==0 ? 1000000000.0 : l/v[i] + t[i] * j;
        }
    }
    rep(i,n){
        double ans=0;
        rep(j,m+1){
            double val = 1;
            rep(k,n)if(i!=k){
                double p_win = 0;
                rep(q,m+1){
                    if(time[i][j]<time[k][q])p_win += prec[k][q];
                }
                val *= p_win;
            }
            ans += val * prec[i][j];
        }
        fcout(8)<<ans<<endl;
    }


    return 0;
}

