    cin >> P[i] >> T[i] >> V[i];
  }

  getncr();

  for(int i=0;i<N;i++){
    double ka = 1.0f;

    if( P[i] == 100 ){
      for(int j=0;j<=M;j++){
	if( V[i] == 0 )
	  Time[i][j] = INF;
	else
	  Time[i][j] = (double)L / (double)V[i] + (double)T[i]*(double)j;	
	pa[i][j] = 0.0f;
      }      
      pa[i][M] = 1.0f;
    } else {
      for(int l=0;l<M;l++){
	ka *= (double)(100-P[i])/100.0;
      }
      //  cout <<i << endl;
      for(int j=0;j<=M;j++){
