	  Time[i][j] = INF;
	else
	  Time[i][j] = (double)L / (double)V[i] + (double)T[i]*(double)j;

	if( ka > 1.0 - EPS ) pa[i][j] = 1.0f;
	else pa[i][j] = ka*ncr[M][j];  

	ka /= (double)(100-P[i])/100.0;
	ka *= (double)P[i]/100.0;       
      }
    }
  }

  for(int i=0;i<N;i++){
    double res = 0.0f;
