	  // cout << H << " " << Time[k][l] << " " << pa[k][l] << endl;
	  if( Time[k][l] > H+EPS ){
	    //      cout << pa[k][l] << endl;
	    kk += pa[k][l];
	  }
	}

	//	cout << "k= " << k << " "<< kk << endl;
	ret *= kk;
      }
