template<typename Field>
struct Comb {
    vector<vector<Field>> c;
    Comb(int n) {
        c.resize(n+1, vector<Field>(n+1));
        for (int i = 0; i <= n; ++i) {
            for (int j = 0; j <= i; ++j) {
                if (j == i || j == 0) {
                    c[i][j] = Field{1};
                } else {
                    c[i][j] = c[i-1][j-1] + c[i-1][j];
                }
            }
        }
    }
    Field operator()(int n, int r) {
        return c[n][r];
    }
};

