		double base_time = V > 0 ? (double)L / V : INT_MAX;
		REP(t_break, M + 1) {
			runner.goal_times.PB(base_time + T * t_break);
			runner.probabilities.PB(
				pow(P, t_break) * pow(1 - P, M - t_break) * combination(M, t_break) );
		}
		runners.PB(runner);
		runner.inspect();
	}

	REP(i, runners.size()) {
		Runner &r = runners[i];
		double ans = 0;
		REP(t, M + 1) {
			double prize_prob = r.probabilities[t];
			REP(j, runners.size()) {
				if (i == j) continue;
				Runner &comp = runners[j];
				double win_prob = 0;
				REP(k, M+1) {
					if(comp.goal_times[k] > r.goal_times[t]) {
						win_prob += comp.probabilities[k];
					}
				}
				prize_prob *= win_prob;
			}
			ans += prize_prob;
		}
		cout << ans << endl;
	}
}

