#define REP(i, n) for (int i = 0; i < (int)(n); i++)
#define FOR(i, s, n) for (int i = (s); i < (int)(n); i++)
#define FOREQ(i, s, n) for (int i = (s); i <= (int)(n); i++)
#define FORIT(it, c) for (__typeof((c).begin())it = (c).begin(); it != (c).end(); it++)
#define MEMSET(v, h) memset((v), h, sizeof(v))

int n, m;
double l;
double p[1000];
double t[1000];
double v[1000];
double prob[2][101][51];
double sum[101][55];
double ans[101];

inline double Sum(int i, int j) { return sum[i][j + 1]; }

int main() {
  while (scanf("%d %d %lf", &n, &m, &l) > 0) {
    MEMSET(prob, 0);
    MEMSET(sum, 0);
    MEMSET(ans, 0);
    REP(i, n) {
      scanf("%lf %lf %lf", &p[i], &t[i], &v[i]);
      p[i] /= 100.0;
      prob[0][i][0] = 1;
      REP(j, m) {
        int prev = j & 1;
        int next = prev ^ 1;
        //MEMSET(prob[next][i], 0);
        FOREQ(k, 0, m) {
          prob[next][i][k] = (1.0 - p[i]) * prob[prev][i][k];
          assert(prob[next][i][k] >= 0.0);
          if (k != 0) {
            prob[next][i][k] += p[i] * prob[prev][i][k - 1];
          }
          assert(prob[next][i][k] >= 0.0);
        }
      }
      FOREQ(j, 0, m) {
        sum[i][j + 1] = sum[i][j] + prob[m & 1][i][j];
      }
    }


    REP(i, n) {
      if (v[i] == 0) { continue; }
      FOREQ(j, 0, m) {
        double time = l / v[i] + t[i] * j;
        if (isnan(time)) { continue; }
        double lans = 1.0;
        REP(k, n) {
          if (i == k || v[k] == 0) { continue; }
          double ntime = l / v[k];
          double lp = 1.0;
          FOREQ(cnt, 0, m) {
            if (ntime  - time > EPS) { break; }
            lp -= prob[m  & 1][k][cnt];
            assert(lp >= -EPS);
            ntime += t[k];
          }
          lans *= lp;
          if (lans == 0) { break; }
        }
        ans[i] += lans * prob[m & 1][i][j];
      }
    }
    REP(i, n) {
      printf("%.8f\n", ans[i]);
    }
  }
}

