LL C(LL n,LL m)
{
    LL a=1,b=1;
    for(int i=m; i>=m-n+1; i--)
        a*=i;
    for(int i=1; i<=n; i++)
        b*=i;
    return a/b;
}

double get(int c,int n,double p)
{
