int N, M, L;
int P[100], T[100], V[100];
double dp[101][52];
double tim[52][52];
double small[52][52];

int main()
{
  cin >> N >> M >> L;
  for(int i = 0; i < N; i++) {
    cin >> P[i] >> T[i] >> V[i];
  }


  for(int i = 0; i < N; i++) {
    memset(small, 0, sizeof(small));
    small[0][0] = 1.0;
    for(int j = 0; j < M; j++) {
      for(int k = 0; k <= j; k++) {
        small[j + 1][k + 1] += small[j][k] / 100.0 * P[i];
        small[j + 1][k]     += small[j][k] / 100.0 * (100 - P[i]);
      }
    }
    for(int j = 0; j <= M; j++) {
      tim[i][j] = (V[i] == 0 ? 1e18 : 1.0 * L / V[i] + j * T[i]);
      dp[i][j] = small[M][j];
    }
  }
  cout << fixed << setprecision(10);

  for(int i = 0; i < N; i++) {
    double ret = 0;
    for(int j = 0; j <= M; j++) {
      double per = dp[i][j];
      for(int k = 0; k < N; k++) {
        if(i == k) continue;
        double qur = 0.0;
        for(int l = 0; l <= M; l++) {
          if(tim[i][j] < tim[k][l]) qur += dp[k][l];
        }
        per *= qur;
      }
      ret += per;
    }
    cout << ret << endl;
  }
}

