        double ans = 0.0;
        for(int i=0; i<=M; i++) {
            double p = (i ? dp[x][M][i] - dp[x][M][i-1] : dp[x][M][i]);
            double tx = 1.0 * L / V[x] + T[x] * i;

            // y  j 
            for(int y=0; y<N; y++) {
                if(x == y) continue;

                bool defeat = false;
                for(int j=0; j<=M; j++) {
                    double ty = 1.0 * L / V[y] + T[y] * j;
                    if(ty - tx > EPS) {
                        // fprintf(stderr, "x = %d, i = %d, y = %d, j = %d: tx = %.12f, ty = %.12f\n", x, i, y, j, tx, ty);
                        // j 
                        double q = (j ? dp[y][M][j-1] : 0);
                        p *= (1 - q);
                        defeat = true;
                        break;
                    }
                }

                if(!defeat) p = 0;
            }
            ans += p;
        }
        printf("%.12f\n", ans);
    }
    return 0;
}

