    comb[i][0] = 1.0;
  }
  for (int i = 1; i <= 50; i++) {
    for (int j = 1; j <= i; j++) {
      comb[i][j] = comb[i-1][j-1] + comb[i-1][j];
    }
  }

  int N, M, L;
