        if (k != i) {
          int l = lower_bound(ts[k].begin(), ts[k].end(), ts[i][j]+EPS) - ts[k].begin();
          if (l != 0) {
            p *= (1.0 - ps_integral[k][l-1]);
          }
        }
      }
      ans += ps[i][j] * p;
    }
    printf("%.6f\n", ans + EPS);
  }
  return 0;
}

