                    comb[j][k]=(p[i]==1 ? 0 : pow(1-p[i],j));
                }
                else if(k==j){
                    comb[j][k]=(p[i]==0 ? 0 : pow(p[i],j));
                }
                else{
                    comb[j][k]=comb[j-1][k-1]*p[i]+comb[j-1][k]*(1-p[i]);
                }
            }
        }
        pos[i]=comb[m];
    }
