using namespace std;

int n, m;
double l;
double p[100], t[100], v[100];
double cb[100][51];

void init(){
	rep(u,n){
		rep(i,m+1){
			int j = m-i;
			cb[u][i] = 1.0;
			int cnt1 = i, cnt2 = j;
			for(int k = 1; k <= i; k++){
				cb[u][i] = cb[u][i]*(m-k+1)/k;
				while(cb[u][i] > 1.0){
					if(cnt1 > 0){
						cb[u][i] *= p[u];
						cnt1--;
					} else if(cnt2 > 0){
						cb[u][i] *= (1-p[u]);
						cnt2--;
					}
					if(cnt1 == 0 && cnt2 == 0) break;
				}
			}
			rep(k,cnt1) cb[u][i] *= p[u];
			rep(k,cnt2) cb[u][i] *= (1-p[u]);
		}
	}
}

int main(){
	cin >> n >> m >> l;
	rep(i,n){
		cin >> p[i] >> t[i] >> v[i];
		p[i] *= 0.01;
	}
	init();
	rep(i,n){
		double pos;
		//double ti;
		double ans = 0.0;
		if(v[i] == 0.0){
			puts("0.00000000000");
			continue;
		}
		for(int j = 0; j <= m; j++){
			pos = cb[i][j];
			rep(k,n){
				if(k == i) continue;
				if(v[k] == 0.0) continue;
				if(t[k] == 0.0){
					if((int)l*(int)v[k]-(int)l*(int)v[i]+(int)t[i]*j*(int)v[i]*(int)v[k] >= 0) pos *= 0.0;
					else pos *= 1.0;
					continue;
				}
				int c;
				if((int)l*(int)v[k]-(int)l*(int)v[i]+(int)t[i]*j*(int)v[i]*(int)v[k] < 0) c = 0;
				else c = ((int)l*(int)v[k]-(int)l*(int)v[i]+(int)t[i]*j*(int)v[i]*(int)v[k])/((int)t[k]*(int)v[i]*(int)v[k]) + 1;
				double tmp = 0.0;
				for(int l = c; l <= m; l++){
					tmp += cb[k][l];
				}
				pos *= tmp;
			}
			ans += pos;
		}
		printf("%.9f\n", ans);
	}
}

#include <algorithm>
#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <vector>
using namespace std;

const int MAX_M = 50;
long long memo[MAX_M + 1][MAX_M + 1];
long long combination(int n, int k) {
	if(k == 0 || k == n)
		return 1;

	if(k * 2 > n)
		k = n - k;

	if(memo[n][k] > 0)
		return memo[n][k];

	return memo[n][k] = combination(n - 1, k - 1) + combination(n - 1, k);
}

int main() {
	int n, m, l;
	scanf("%d %d %d", &n, &m, &l);

	vector<int> t(n), v(n);
	vector<double> p(n);
	for(int i = 0; i < n; ++i) {
		scanf("%lf %d %d", &p[i], &t[i], &v[i]);
		p[i] /= 100.0;
	}

	vector<vector<double>> time(n, vector<double>(m + 1));
	for(int i = 0; i < n; ++i) {
		time[i][0] = static_cast<double>(l) / v[i];
		for(int j = 1; j <= m; ++j) {
			time[i][j] = time[i][j - 1] + t[i];
		}
	}

	vector<vector<double>> probability(n, vector<double>(m + 1));
	for(int i = 0; i < n; ++i) {
		const double q = 1.0 - p[i];
		for(int j = 0; j <= m; ++j) {
			probability[i][j] = combination(m, j) * pow(p[i], j) * pow(q, m - j);
		}
	}

	vector<vector<double>> sum(probability);
	for(int i = 0; i < n; ++i) {
		for(int j = m - 1; j >= 0; --j) {
			sum[i][j] += sum[i][j + 1];
		}
	}

	for(int i = 0; i < n; ++i) {
		double ans = 0.0;
		for(int j = 0; j <= m; ++j) {
			double tmp = probability[i][j];
			for(int k = 0; k < n; ++k) {
				if(i == k) continue;

				const int idx = upper_bound(time[k].begin(), time[k].end(), time[i][j]) - time[k].begin();
				tmp *= sum[k][idx];
			}
			ans += tmp;
		}

		printf("%.8lf\n", ans);
	}

	return EXIT_SUCCESS;
}

