using namespace std;
const int MAXN=100+10;
double P[MAXN],T[MAXN],V[MAXN];
double late[MAXN][MAXN];
double C[MAXN][MAXN];
int n,m,l;
int main()
{
//    freopen("in","r",stdin);
    for(int n=0; n<=50;n++)
    {
        C[n][0]=1;
        for(int k=0; k<n; k++)
            C[n][k+1]=C[n][k]*(n-k)/(k+1.0);
    }

    while(~scanf("%d%d%d",&n,&m,&l))
    {
        for(int i=0; i<n; i++)
            scanf("%lf%lf%lf",P+i,T+i,V+i),P[i]/=100;

        for(int i=0; i<n; i++)
        {
            for(int j=0; j<=m; j++)
            {
                late[i][j]=C[m][j]*pow(P[i],j)*pow(1-P[i],m-j);
//                printf("late[%d][%d]=%f ",i,j,late[i][j]);
            }
//            putchar(10);
        }
        for(int i=0; i<n; i++)
        {
            double ans=0,t=l/V[i];
            for(int k=0; k<=m; k++)
            {
                double one=late[i][k];
                for(int j=0; j<n && one; j++)
                {
                    if(i==j) continue;
                    double jp=0;
                    for(int kk=0; kk<=m ; kk++)
                        if(kk*T[j]+l/V[j]>t)
                            jp+=late[j][kk];
                    one*=jp;
                }
                ans+=one;
//                printf("%d %d %.8f\n",i,k,one);
                t+=T[i];
            }
            printf("%.8f\n",ans);
        }
    }
    return 0;
}

