using namespace std;

typedef long long LL;
const LL maxn = 111;
LL v[maxn], t[maxn];
LL c[maxn][maxn];

double p[maxn];
double lv[maxn];
LL n, m, l;

double ff (LL u, LL state, LL v) {
	double ret = 0;
	for (LL i = 0; i <= m; ++i) {
		if (lv[v] + i * t[v] > lv[u] + state * t[u]) {
			ret += 1.0 * c[i][m] * pow (1.0 * p[v], 1.0*i) * pow (1.0 - p[v], 1.0*m - i);
		}
	}
	return ret;
}
double f (LL u, LL state) {
	double ret = 1.0 * c[state][m] * pow (1.0 * p[u], 1.0 * state) * pow (1.0 - p[u], 1.0 * m - state);
	for (LL i = 0; i < n; ++i) {
		if (i == u) continue;
		ret *= ff (u, state, i);
	}
	return ret;
}
double solve (LL u) {
	double ret = 0;
	for (LL i = 0; i <= m; ++i) {
		ret += f (u, i);
	}
	return ret;
}

//C(m, n)
LL combination (LL m, LL n) {
	LL ret = 1;
	LL up[maxn], low[maxn];
	for (LL i = 1; i <= n; ++i) low[i] = up[i] = i;
	for (LL i = m + 1; i <= n; ++i) {
		for (LL j = 1; j <= n - m; ++j) {
			if (up[i] % low[j] == 0) {
				up[i] /= low[j];
				low[j] = 1;
			}
		}
	}
	for (LL i = 1; i <= n; ++i) ret *= up[i];
	for (LL i = 1; i <= n - m; ++i) ret /= low[i];
	return ret;
}
int main () {
	LL i;
	while (~scanf ("%lld%lld%lld", &n, &m, &l)) {
		//c[i][m]&#32452;
		for (i = 0; i <= (m >> 1); ++i) {
			c[i][m] = c[m-i][m] = combination (i, m);
		}
		for (i = 0; i < n; ++i) {
			scanf ("%lf%lld%lld", &p[i], &t[i], &v[i]);
			lv[i] = 1.0 * l / v[i];
			p[i] /= 100;
		}
		for (i = 0; i < n; ++i) {
			double ans = solve (i);
			printf ("%.8lf\n", ans);
		}
	}
	return 0;
}

