		for(int j = m - 1; j >= 0; j--) sum[i][j] = sum[i][j + 1] + dp[i][j];
		/*
		rep(j, m + 1) cerr<<dp[i][j]<<(j==m?"\n":" ");
		rep(j, m + 1) cerr<<sum[i][j]<<(j==m?"\n":" ");
		*/
	}

	rep(i, n){
		double s = 0;
		rep(j, m + 1){
			double e = dp[i][j];
			rep(k, n) if(k != i){
				int tm = (int)ceil((l * 1.0 / v[i] - l * 1.0 / v[k] + t[i] * j) / t[k] + EPS);

				tm = max(tm, 0);
				if(t[k] == 0) tm = l * 1.0 / v[i] - l * 1.0 / v[k] + t[i] * j < - EPS ? 0 : m + 1;
				if(tm <= m) e *= sum[k][tm];
				else e = 0;
			}
			s += e;
		}
		printf("%.9f\n", s);
	}

	return 0;
}

