using namespace std;

int main(){
	int N, M, L;
	int P[100], T[100], V[100];
	// [P][M][lest]
	static double dp[101][101][101];
	for(int i=0;i<=100;i++){
		for(int j=0;j<=100;j++)
			for(int k=0;k<=100;k++) dp[i][j][k] = 0.0;
		dp[i][0][0] = 1.0;
		for(int j=0;j<100;j++){
			for(int k=0;k<=j;k++){
				dp[i][j+1][k  ] += dp[i][j][k]*(100-i)/100.0;
				dp[i][j+1][k+1] += dp[i][j][k]*i/100.0;
			}
		}
	}
	while(cin >> N >> M >> L){
		for(int i=0;i<N;i++) cin >> P[i] >> T[i] >> V[i];
		for(int i=0;i<N;i++){
			double res = 0.0;
			for(int j=0;j<=M;j++){
				double cur = dp[P[i]][M][j];
				double time = (double)L/V[i] + j*T[i];
				for(int k=0;k<N;k++){
					if(i==k) continue;
					double sum = 0.0;
					for(int l=0;l<=M;l++){
						double otime = (double)L/V[k] + l*T[k];
						if(otime - time < 1e-8) continue;
						sum += dp[P[k]][M][l];
					}
					cur *= sum;
				}
				res += cur;
			}
			printf("%.8lf\n", res);
		}
	}
}

