lint gcd(lint p, lint q){
    p = abs(p);
    q = abs(q);
    while(q){
        lint r = p % q;
        p = q;
        q = r;
    }
    return p;
}

typedef struct Fraction{
    lint n, d; // n / d

    Fraction(lint n_=0, lint d_=1): n(n_), d(d_){
        if(d == 0) n = (n < 0 ? -1 : 1);
        else{
            lint g = gcd(n, d);
            n /= g;
            d /= g;
        }
    }

    Fraction operator-() const{
        return Fraction(-n, d);
    }
    Fraction &operator+=(const Fraction &rhs){
        n = n * rhs.d + rhs.n * d;
        d = d * rhs.d;
        return *this = Fraction(n, d);
    }
    Fraction &operator-=(const Fraction &rhs){
        n = n * rhs.d - rhs.n * d;
        d = d * rhs.d;
        return *this = Fraction(n, d);
    }
    Fraction &operator*=(const Fraction &rhs){
        n = n * rhs.n;
        d = d * rhs.d;
        return *this = Fraction(n, d);
    }
    Fraction &operator/=(const Fraction &rhs){
        n = n * rhs.d;
        d = d * rhs.n;
        return *this = Fraction(n, d);
    }

    Fraction operator+(const Fraction &rhs) const{
        Fraction ret(*this);
        return ret += rhs;
    }
    Fraction operator-(const Fraction &rhs) const{
        Fraction ret(*this);
        return ret -= rhs;
    }
    Fraction operator*(const Fraction &rhs) const{
        Fraction ret(*this);
        return ret *= rhs;
    }
    Fraction operator/(const Fraction &rhs) const{
        Fraction ret(*this);
        return ret /= rhs;
    }

    bool operator==(const Fraction &rhs) const{
        return n == rhs.n && d == rhs.d;
    }
    bool operator!=(const Fraction &rhs) const{
        return !(*this == rhs);
    }

    bool operator<(const Fraction &rhs) const{
        return n * rhs.d < rhs.n * d;
    }
    bool operator>(const Fraction &rhs) const{
        return rhs < *this;
    }
    bool operator<=(const Fraction &rhs) const{
        return !(*this > rhs);
    }
    bool operator>=(const Fraction &rhs) const{
        return !(*this < rhs);
    }

}frac;

