	{
		cin>>p[i]>>t[i]>>v[i];
		p[i]/=100;
		if(p[i]==1)
		{
			c[m][i]=1;
		}
		else if(p[i]==0)
		{
			c[0][i]=1;
		}
		else
		{
			c[0][i]=1;
			for(int j=0;j<m;j++)c[0][i]*=1-p[i];
			for(int j=0;j<m;j++)c[j+1][i]=c[j][i]*(m-j)/(j+1)/(1/p[i]-1);
		}
	}
	for(int i=0;i<n;i++)
	{
		double ans=0;
