void solve(){
    int n,m,l;
    cin >> n >> m >> l;
    vector<long double> p(n),t(n),v(n);
    rep(i,0,n){
        cin >> p[i] >> t[i] >> v[i];
        if(v[i]==0) v[i]=1e-12;
    }

    ll comb[51][51]={};
    rep(i,0,51){
        comb[i][i]=1;
        comb[i][0]=1;
    }
    rep(i,1,51) rep(j,1,i){
        if(i>0 and i>j) comb[i][j]+=comb[i-1][j];
        if(j>0) comb[i][j]+=comb[i-1][j-1];
    }

    vector<map<int,long double>> x(n);
    rep(i,0,n) rep(j,0,m+1) x[i][t[i]*j]+=powl((100-p[i])/100,m-j)*comb[m][j]*powl(p[i]/100,j);
