                for(const auto& p2:x[j]) if(p2.first+l/v[j]>p1.first+l/v[i]) s2+=p2.second;
                tmp*=s2;
            }
            s1+=tmp;
        }
        cout << fixed << setprecision(8) << s1 << endl;
    }
}

