using ar3 = array<int, 3>;

ld comb(ll l, ll r) {
	ld res = 1;
	REP(i, r) {
		res *= l - i;
		res /= i + 1;
	}
	return res;
}

// [person][rest-number] -> prob
vector<vector<ld>> calc_prob_table(const vector<ar3>& info, const int M) {
	const int N = info.size();
	vector<vector<ld>> res(N, vector<ld>(M + 1));
	REP(i, N)REP(j, M + 1) {
		ld rest = info[i][0];
		res[i][j] = comb(M, j)*powl(rest / 100., j)*powl(1 - rest / 100., M - j);
	}
	//REP(i, N)REP(j, M - 1)res[i][j + 1] += res[i][j];
	return res;
}

ld lose_prob(const vector<ld>& prob, ar3 info, ld time, const ld L) {
	const int M = prob.size();
	time -= L / info[2];
	if (info[1] == 0) {
		time = INF * (time >= 0 ? 1 : -1);
	}
	else {
		time /= (ld)info[1];
	}

	ld res = 0;
	REP(i, min(time + 1e-3, (ld)M)) {
		res += prob[i];
	}
	return 1 - res;
}

int main()
{
	int N, M, L;
	cin >> N >> M >> L;
	vector<ar3> info(N);
	REP(i, N)REP(j, 3)cin >> info[i][j];

	if (N == 1) {
		cout << 1 << endl;
		return 0;
	}

	auto table = calc_prob_table(info, M);
	REP(i, N) {
		ld res = 0;
		REP(rest, M + 1) {
			ld tres = table[i][rest];
			ld time = (ld)L / info[i][2] + info[i][1] * rest;
			REP(opp, N)if (opp != i) {
				tres *= lose_prob(table[opp], info[opp], time, L);
			}
			res += tres;
		}
		cout << fixed << setprecision(10) << res << endl;
	}

	return 0;
}

