int dlcmp(double x) {return x<-eps?-1:x>eps;}

double p[N],speed[N],rest[N][N],sum[N][N];
double time[N];
int n,m;
double l;

void calc(double p,double s[])
{
	int i,j;

	memset(rest,0,sizeof(rest));
	rest[0][0]=1;
	for (i=1;i<=m;i++)
		for (j=0;j<=i;j++)
		{
			rest[i][j]+=rest[i-1][j]*(1-p);
			if (j-1>=0)
				rest[i][j]+=rest[i-1][j-1]*p;
		}


	for (i=m;i>=0;i--)
		s[i]=s[i+1]+rest[m][i];
}

int main()
{
	int i,j,k;
	double ans,curT;

	while (scanf("%d%d%lf",&n,&m,&l)!=EOF)
	{
		memset(sum,0,sizeof(sum));
		for (i=1;i<=n;i++)
		{
			scanf("%lf%lf%lf",&p[i],&time[i],&speed[i]);
			p[i]/=100;

			calc(p[i],sum[i]);
		}

		/*
		for (i=1;i<=n;i++,printf("\n"))
			for (j=0;j<=m;j++)
				printf("%.3f ",sum[i][j]);
		*/

		for (i=1;i<=n;i++)
		{
			ans=0;
			for (j=0;j<=m;j++)
			{
				curT=l/speed[i]+time[i]*j;
				double tmp=sum[i][j]-sum[i][j+1];
				int flag=1;

				for (k=1;k<=n;k++)
					if (k!=i)
					{
						double r=curT-l/speed[k];

						int idx=r/time[k];
						idx=max(0,idx);
						while (dlcmp(idx*time[k]-r)<=0&&idx<=m)	idx++;

						if (idx>m)
						{
							flag=0;
							break;
						}

						tmp*=sum[k][idx];
					}

				if (flag)
					ans+=tmp;
			}

			printf("%.8f\n",ans);
		}
	}

	return 0;
}

