    for (int j = 0; j <= M; j++) {
      double tmp = 1.0;
      for (int k = 0; k < j; k++) {
        tmp *= P[i] / 100.0;
      }
      for (int k = 0; k < M - j; k++) {
        tmp *= (100 - P[i]) / 100.0;
      }
      pos[i][j] = comb[M][j] * tmp; 
    }
    cpos[i][M] = pos[i][M];
    for (int j = M-1; j >= 0; j--) {
      cpos[i][j] = pos[i][j] + cpos[i][j+1];
    }
  }
