int get_time(double L, double V, double T, double A)
{
    return L/V + T*A;
}

int main()
{
    int N, M;
    double L;
    cin >> N >> M >> L;
    vector<double> P(N), T(N), V(N);
    for (int i = 0; i < N; i++) {
        cin >> P[i] >> T[i] >> V[i];
        P[i] /= 100;
    }
    double p[MAX_N][MAX_M+2][MAX_M+2];
    memset(p, 0, sizeof(p));

    for (int i = 0; i < N; i++) {
        p[i][0][0] = 1;
        for (int j = 0; j < M; j++) {
            for (int k = 0; k < M; k++) {
                p[i][j+1][k]   += p[i][j][k] * (1 - P[i]);
                p[i][j+1][k+1] += p[i][j][k] * P[i];
            }
        }
    }

    for (int i = 0; i < N; i++) {
        double res = 0;
        for (int j = 0; j <= M; j++) {
            double t = get_time(L, V[i], T[i], j);
            double r = 1;
            bool win = 1;
            for (int k = 0; k < N; k++) {
                if (i == k) {
                    continue;
                }
                double rr = 0;
                bool ok = 0;
                for (int l = 0; l <= M; l++) {
                    double t2 = get_time(L, V[k], T[k], l);
                    if (t < t2) {
                        ok = 1;
                        rr += p[k][M][l];
                    } 
                }
                if (!ok) {
                    win = 0;
                    break;
                }
                r *= rr;
            }
            res += (win ? r * p[i][M][j] : 0);
        }
        printf("%.8f\n", res);
    }
    return 0;
}

