#define LL long long

LL comb(LL n,LL m){
	LL ret=1,i;
	m=m<(n-m)?m:(n-m);
	for (i=n-m+1;i<=n;ret*=(i++));
	for (i=1;i<=m;ret/=(i++));
	return m<0?0:ret;
}

double a[Max][Max], aa[Max][Max];

struct node
{
	double p, time, v;
}Node[Max], Nodeb[Max];

double ppow (double p, LL num)
{
	double ans = 1.0;
	for (LL i = 1;i <= num;i ++)
		ans = ans * p;
	return ans;
}

int main ()
{
	LL n, m, L, i, j, k, q, num;
	while (scanf ("%lld%lld%lld", &n, &m, &L) == 3)
	{
		for (i = 0;i < n;i ++)
		{
			scanf ("%lf%lf%lf", &Node[i].p, &Node[i].time, &Node[i].v);
			Node[i].p /= 100.0;
		}
		memset (a, 0, sizeof (a));
		memset (aa, 0, sizeof (aa));
		for (i = 0;i < n;i ++)
		{
			for (j = 0;j <= m;j ++)
			{
				num = comb(m, j);
				a[i][j] = (double)num * ppow (Node[i].p, j) * ppow((1 - Node[i].p), m - j);
				aa[i][j] = (double)j * Node[i].time + 1.0 * L / Node[i].v;
			}
		}

		double res = 0.0;
