			{
				tmp = a[i][j];
				for(k = 0 ; k < n ; k++)
				{
					if( k == i ) continue;

					double t = 0.0;
					for(q = 0 ; q <= m ; q++)
					{
						if( aa[k][q]-aa[i][j] > eps )
							t += a[k][q];
					}
					tmp *= t;
				}
				res += tmp;
			}
			printf("%.12lf\n", res);
		}
	}
}

