    {
        cin >> P[i] >> T[i] >> V[i];
        P[i] /= 100;
    }
    // comb[i][j] := iCj
    ld comb[60][60];
    for (int i = 0; i <= 50; i++)
        comb[i][0] = 1.0;
    for (int i = 1; i <= 50; i++)
    {
        for (int j = 1; j <= i; j++)
        {
            comb[i][j] = comb[i - 1][j - 1] + comb[i - 1][j];
        }
    }
    // pn[i][j] := i  j 
    // tn[i][j] := i  j  (L/V[i] + j*T)
    vector<vector<ld>> pn(N, vector<ld>(M + 1)), tn(N, vector<ld>(M + 1));
    for (int i = 0; i < N; i++)
    {
        for (int j = 0; j <= M; j++)
        {
            tn[i][j] = ld(L) / V[i] + j * T[i];
            pn[i][j] = comb[M][j] * pow(P[i], j) * pow(1.0 - P[i], M - j);
        }
    }
    for (int i = 0; i < N; i++)
    {
        if (abs(V[i]) < EPS)
        {
            cout << fixed << setprecision(10) << 0.0 << endl;
            continue;
        }
        ld res = 0;
        for (int j = 0; j <= M; j++)
        {
            ld p = pn[i][j];
            for (int k = 0; k < N; k++)
            {
                if (k == i)
                    continue;
                if (abs(V[i]) < EPS)
                    continue;
                ld sum = 0;
                for (int l = 0; l <= M; l++)
                {
                    if (tn[k][l] > tn[i][j] + EPS)
                        sum += pn[k][l];
                }
                p *= sum;
            }
            res += p;
        }
        cout << fixed << setprecision(10) << res << endl;
    }
}

