            me[i].po[j] = solve(me[i].p / 100.0,j - 1,m) * f[m][j - 1];
            me[i].posum[j] = me[i].posum[j - 1] + me[i].po[j];
            }
            me[i].posum[m + 1] = 1;
        }
            for(i = 1; i <= n; i++)
            {
                me[i].ans = 0;
                for(j = 1; j <= m + 1; j++)
                {
                    ss = me[i].po[j];
                    for(k = 1; k <= n; k++)
                    if(i == k);
                    else
                    {
                        flag = 0;
                        for(x = 1; x <= m + 1; x++)
                        if(me[i].time[j] < me[k].time[x])
                        {
                        ss *= (1 - me[k].posum[x] + me[k].po[x]);
                        flag = 1;
                        break;
                        }
                        if(flag == 0)
                        break;
                    }
                    if(flag == 0)
                    ss = 0;
                    me[i].ans += ss;
                }
            }
            for(i = 1; i <= n; i++)
            printf("%lf\n",me[i].ans);
    }
    return 0;
}

