typedef int64_t i64;
const int NMAX = 1000;
const long double eps = 1e-9;

int N, M, L;
int P[NMAX], T[NMAX], V[NMAX];
long double answer[NMAX];
vector<long double> value[NMAX], prob[NMAX];

int getSign(long double value) {
	if (value < -eps)
		return -1;
	if (value > eps)
		return 1;
	return 0;
}

i64 repeatedPerm(int a, int b) {
	unordered_map<int, int> primeFact;
	int c = a + b;
	for (int i = 2; i <= c; ++i) {
		int currNum = i;
		for (int j = 2; j <= sqrt(currNum) + 0.5; j += 2) {
			while (currNum % j == 0) {
				++primeFact[j];
				currNum /= j;
			}
			if (j == 2)
				j = 1;
		}
		if (currNum > 1)
			++primeFact[currNum];
	}
	for (int i = 2; i <= a; ++i) {
		int currNum = i;
		for (int j = 2; j <= sqrt(currNum) + 0.5; j += 2) {
			while (currNum % j == 0) {
				--primeFact[j];
				currNum /= j;
			}
			if (j == 2)
				j = 1;
		}
		if (currNum > 1)
			--primeFact[currNum];
	}
	for (int i = 2; i <= b; ++i) {
		int currNum = i;
		for (int j = 2; j <= sqrt(currNum) + 0.5; j += 2) {
			while (currNum % j == 0) {
				--primeFact[j];
				currNum /= j;
			}
			if (j == 2)
				j = 1;
		}
		if (currNum > 1)
			--primeFact[currNum];
	}

	i64 answer = 1;
	for (auto it: primeFact) {
		int curr = it.second;
		while (curr--)
			answer *= it.first;
	}
	return answer;
}

int main() {
//	assert(freopen("debug.in", "r", stdin));
//	assert(freopen("debug.out", "w", stdout));

	int i, j, k, l;

	cin >> N >> M >> L;
	for (i = 1; i <= N; ++i)
		cin >> P[i] >> T[i] >> V[i];

	for (i = 1; i <= N; ++i) {
		long double time = L / (long double)V[i];
		for (j = 0; j <= M; ++j, time += T[i]) {
			i64 currCases = repeatedPerm(j, M - j);
			long double probability = pow(P[i] / (long double)100, j) * pow(1 - P[i] / (long double)100, M - j);
			prob[i].push_back(probability * currCases);
			value[i].push_back(time);
		}
	}

	for (i = 1; i <= N; ++i) {
		for (j = 0; j <= M; ++j) {
			long double currProb = prob[i][j];
			for (k = 1; k <= N; ++k) {
				if (i == k)
					continue;
				long double nowProb = 0;
				for (l = M; l >= 0 && value[k][l] > value[i][j]; --l)
					nowProb += prob[k][l];
				currProb *= nowProb;
			}
			answer[i] += currProb;
		}
	}

	cout << fixed << setprecision(8);
	for (i = 1; i <= N; ++i)
		cout << answer[i] << '\n';

	return 0;
}

