double C(int a,int b)
{
	b=std::min(a-b,b);
	double r=1;
	for(int i=1;i<=b;++i)r=r*(a-b+i)/i;
	return r;
}
double F(double a,int b)
{
	double r=1,x=a;
	for(;b;b>>=1)
	{
		if(b&1)r*=x;
		x*=x;
	}
	return r;
}
