const int INF=1000000000;

vector<runner> vr;
double mem[101][51][101];
// kxe
double calcPer(int n,int k,int idx){
	if(!EQ(mem[n][k][idx],INF))
		return mem[n][k][idx];
	double p = vr[idx].p;
	if(k==0)
		return 1.0;
    return mem[n][k][idx]=(((double)n/k)*calcPer(n-1,k-1,p)*p);
}

int main(){
	int n,m,L;
	for(int i = 0; i < 101; i++)
		for(int j = 0; j < 51; j++)
			for(int k = 0; k < 101; k++)
				mem[i][j][k]=INF;
	cin>>n>>m>>L;
	for(int i = 0; i < n; i++){
		runner r;
		int p;
