		double sumPer=0.0;
		if(vr[i].v!=0){
			// xe
			for(int j = 0; j <= m; j++){
				// xexem
				double cper=calcPer(m,j,i)*pow((double)(1-vr[i].p),m-j);
				double selfTime=((double)L/vr[i].v)+vr[i].t*j;
				// G
				for(int k = 0; k < n; k++){
