						double enemyTime=((double)L/vr[k].v)+vr[k].t*l;
						// GBBI
						if(!EQ(enemyTime,selfTime)&&enemyTime>selfTime)
							break;
						// xem
                        double p=calcPer(m,l,k)*pow((double)(1-vr[k].p),m-l);
						enemPer-=p;
					}
					// cmAGxBm
					cper*=enemPer;
				}
				// cperm
