ld dp[55][55];

ld cal(ld p, int n, int k)
{
	if (n == 0) return(k == 0 ? 1.0 : 0);
	if (dp[n][k] >= 0) return dp[n][k];
	ld l = (1 - p)*cal(p, n - 1, k);
	ld r = p*cal(p, n - 1, k - 1);
	return dp[n][k] = l + r;
}


