#include <bits/stdc++.h>
using namespace std;
#define rep(i,a,b) for(int i=a;i<b;i++)
#define rrep(i,a,b) for(int i=a;i>=b;i--)
#define INF INT_MAX/2
#define EPS 1e-10

typedef long long ll;
typedef vector<int> vi;
typedef vector<double> vd;
typedef vector<vd> vvd;

double calc(double r, int a, int b)
{
	double ret = r;
	b = min(a - b, b);
	if (b == 0) return ret;
	rep(i, 1, b + 1) ret *= (double)(a - i + 1) / i;
	return ret;
}

int main()
{
	int N, M, L;
	cin >> N >> M >> L;
	vi P(N), T(N), V(N);
	rep(i, 0, N) cin >> P[i] >> T[i] >> V[i];

	vvd per(N, vd(M+1, 0));
	rep(i, 0, N)
	{
		double p = P[i] / 100.0;
		double pp = 1.0 - p;
		rep(j, 0, M + 1) per[i][j] = calc(pow(pp, M - j) * pow(p, j), M, j);
	}

	vvd imos(N, vd(M + 2, 0));
	rep(i, 0, N)
	{
		rep(j, 0, M + 1) imos[i][j] = per[i][j];
		rrep(j, M, 0) imos[i][j] += imos[i][j + 1];
	}

	rep(i, 0, N)
	{
		double ans = 0;
		rep(j, 0, M + 1)
		{
			double _ans = per[i][j];
			if (V[i] == 0) continue;
			double deadline = (double)L / V[i] + (double)T[i] * j;

			rep(k, 0, N)
			{
				if (i == k) continue;
				if (V[i] == 0) continue;
				double fastest = (double)L / V[k];
				if (deadline < fastest - EPS) continue;

				if (T[k] == 0)
				{
					_ans = 0;
					continue;
				}

				int minrest = (deadline - fastest + T[k] - 1) / T[k];
				double test = fastest + T[k] * minrest;
				if (abs(deadline - test) < EPS) minrest++;
				if (M < minrest)
				{
					_ans = 0;
					continue;
				}
				_ans *= imos[k][minrest];
			}

			ans += _ans;
		}
		printf("%.10f\n", ans);
	}
}

