    double p = P[i] / 100.0;
    rep(j,M+1){
      TM[i].push_back( L / V[i] + j * T[i] );
      PB[i].push_back( C[M][j] * pow(p,j) * pow(1-p,M-j) );
    }
  }

  rep(sp,N){
    double ans = 0;
    rep(i,TM[sp].size()){
      double cost = 1;
      rep(gp,N){
	double cost2 = 0;
	if(sp == gp)continue;
	rep(j,TM[gp].size()){
	  if(TM[sp][i] >= TM[gp][j]){
	    cost2 += PB[gp][j];
	  } else break;
	}
	cost *= ( 1 - cost2 );
      }
      ans += cost * PB[sp][i];
    }
    printf("%.10f\n",ans);
  }

  return 0;
}

