double nCr(int n, int r){
  if(r==0||n==r)return 1;
  double a=1;
  for(int i=0;i<r;i++) a*=n-i;
  for(int i=1;i<=r;i++)a/=i;
  return a;
}

int n,m;
double DP(){
  double dp[N][M]={},sum=0;
  for(int i=0;i<=m;i++){
    dp[0][i]=A[0][m-i]*B[0][i]*nCr(m,i);
    sum+=dp[0][i];
  }
  for(int i=1;i<n;i++)
    for(int j=0;j<=m;j++)
      for(int k=0;k<=m;k++)
	if(d[i]+j*t[0]<d[i]+k*t[i]) 
	  dp[i][j]+=dp[i-1][j]*A[i][m-k]*B[i][k]*nCr(m,k);

  double res=0;
  for(int i=0;i<=m;i++)res+=dp[n-1][i];
  return res;
}

