#include <iostream>
#include <iomanip>
using namespace std;

int N, M, L;
int P[100], T[100], V[100];
double prob[55][100];

void calc(int x)
{
	double p = P[x] / 100.0;
	double rest[55][55];

	for(int i=0; i<=M; i++)
	for(int j=0; j<=M; j++)
		rest[j][i] = 0.0;

	rest[0][0] = 1.0;

	for(int i=0; i<M; i++)
	for(int j=0; j<M; j++)
	{
		rest[i+1][j] += rest[i][j] * (1-p);
		rest[i+1][j+1] += rest[i][j] * p;
	}


	for(int i=0; i<=M; i++)
		prob[i][x] = rest[M][i];
}

void solve()
{
	for(int i=0; i<N; i++)
		calc(i);

	for(int i=0; i<N; i++)
	{
		double res = 0.0;
		for(int j=0; j<=M; j++)
		{
			double p = prob[j][i];
			double ltm = 1.0*L/V[i] + (T[i]*j);

			for(int k=0; k<N; k++)
			{
				if(k==i) continue;

				double t = 0.0;
				for(int l=0; l<=M; l++)
				{
					double rtm = 1.0*L/V[k] + (T[k]*l);
					if(ltm < rtm) t += prob[l][k];
				}

				p *= t;
			}

			res += p;
		}

		cout.setf(ios::fixed);
		cout.precision(11);
		cout << res << endl;
	}
}



int main()
{

	cin >> N >> M >> L;

	for(int i=0; i<N; i++)
		cin >> P[i] >> T[i] >> V[i];

	solve();

}

