    }

    vector<vector<double> > c(m+1, vector<double>(m+1, 0));
    for(int i=0; i<=m; i++) c[i][0] = 1;
    for(int i=1; i<=m; i++){
        for(int j=1; j<=i; j++){
            c[i][j] = c[i-1][j-1] +c[i-1][j];
        }
    }

    vector<vector<double> > prob(n, vector<double>(m+1)), time(n, vector<double>(m+1));
    for(int i=0; i<n; i++){
        double ttime = l/v[i];
        for(int j=0; j<=m; j++){
            prob[i][j] = pow(p[i], j) *pow(1-p[i], m-j) *c[m][j];
            time[i][j] = ttime;
            ttime += t[i];
        }
    }
    vector<vector<double> > accumprob = prob;
    for(int i=0; i<n; i++){
        for(int j=m-1; j>=0; j--){
            accumprob[i][j] += accumprob[i][j+1];
        }
