double outt(double p1,int now)
{
    int i,h;double ta;ta=1;
    for (i=1;i<=now;i++) ta*=p1;
    for (i=1;i<=m-now;i++) ta*=(1-p1);
    h=m-now;
    if (m-h<h) h=m-h;
    for (i=1;i<=h;i++)
    {
