double combi(int n,int r){
    if(n<r||n<0||r<0) return 0;
    return fact[n]/fact[n-r]/fact[r];
}


void set_combi(){
    double s=1.0;
    fact.push_back(1.0);
    for(int i=1;i<=50;i++){
        s*=i;
        fact.push_back(s);
    }
