            {
                double tmp=peo[i].pp[j];
                for(int k=0; k<n; k++)
                {
                    if(k==i)
                        continue;
                    double t=0;
                    for(int l=0; l<=m; l++)
                        if(peo[k].time[l]>peo[i].time[j])
                            t+=peo[k].pp[l];
                    tmp*=t;
                }
                ans+=tmp;
            }
            printf("%.8lf\n",ans);
        }
    }
    return 0;
}

