typedef long long lint;
const int maxint = -1u>>1;
const double esp = 1e-13;
const int maxn = 100 + 10;
const int maxm = 50 + 10;
double p[maxn], t[maxn], v[maxn];
int n, m, L;
double con[maxm][maxm];
double pow_p[maxn][maxm], pow_np[maxn][maxm], f[maxn][maxm], sum[maxn][maxm];
double ans[maxn];
double times[maxn][maxm];
int zore[maxn];
int sgn(double x) {
    return (x > esp) - (x < -esp);
}
bool cmp(const double &a, const double &b) {
    return sgn(a - b) < 0;
}
void init() {
    rep (i, n) {
        scanf("%lf%lf%lf", &p[i], &t[i], &v[i]);
        p[i] /= 100;
        zore[i] = 0;
        if(sgn(v[i]) == 0) {
            zore[i] = 1;
        }
    }
    repf (i, 1, m) {
        con[i][0] = con[i][i] = 1;
        repf (j, 1, i - 1)
            con[i][j] = con[i - 1][j] + con[i - 1][j - 1];
    }

    rep (i, n) {
        if(zore[i]) continue;
        pow_p[i][0] = 1;
        pow_np[i][0] = 1;
        times[i][0] = L * 1.0 / v[i];
        repf (j, 1, m) {
            pow_p[i][j] = pow_p[i][j - 1] * p[i];
            pow_np[i][j] = pow_np[i][j - 1] * (1 - p[i]);
            times[i][j] = times[i][j - 1] + t[i];
        }
    }

    rep (i, n) {
        if(zore[i]) continue;
        sum[i][0] = f[i][0] = con[m][0] * pow_np[i][m] * pow_p[i][0];
        repf (j, 1, m) {
            f[i][j] = con[m][j] * pow_np[i][m - j] * pow_p[i][j];
            sum[i][j] = sum[i][j - 1] + f[i][j];
        }
    }
}
double gao(double lim, int w) {
    int h = m + 1;
    int hh = upper_bound(times[w], times[w] + m + 1, lim, cmp) - times[w];
    if(zore[w]) return 1;
    rep (i, m + 1)
        if(sgn(times[w][i] - lim) > 0) {
            h = i;
            break;
        }
    //printf("%d %d %f %f %f %f\n", h, hh, lim, times[w][h], times[w][hh], 1 - sum[w][h - 1]);
    //out(h);
    if(h - 1 < 0)  return 1;
    else return 1 - sum[w][h - 1];
}
int main()  {
    int blank = 0;
    while( scanf("%d%d%d", &n, &m, &L) == 3) {
        if(blank) printf("\n");
        init();
        //if(n == 1) {
            //printf("%.8f\n", 1.0);
            //continue;
        //}
        rep (i, n) {
            ans[i] = 0;
            if(!zore[i]) 
                repf (j, 0, m) {
                    double res = 1;
                    rep (k, n) {
                        if(k == i) continue;
                        res *= gao(times[i][j], k);
                        //if(i == 1) printf("%f %f %d\n", gao(times[i][j], k), times[i][j], k);
                    }
                    //printf("%f %f %d %d\n", res, f[i][j], i, j);
                    ans[i] += res * f[i][j];
                }
            printf("%.12f", ans[i]);
            if(i != n - 1) printf("\n");
        }
        blank = 1;
    }
    return 0;
}

#include <iostream>
