typedef vector<double> vd;

const ll M=1000000000;

class Runner {
public:
	vd goal_times;
	vd probabilities;

	Runner() {
		goal_times = vd();
		probabilities = vd();
	}

	void inspect() {
		cerr << "goal_times" << endl;
		REP(i, goal_times.size()) {
			cerr << " " << goal_times[i];
		}
		cerr << endl;
		cerr << "probabilities" << endl;
		REP(i, probabilities.size()) {
			cerr << " " << probabilities[i];
		}
		cerr << endl;
	}
};


ll combination(int n,int k){
	ll ans=1;
	FOR(i,1,k+1){
		ans*=n-i+1;
		ans/=i;
	}
	return ans;
}

const double EPS = 1e-9;
int main()
{
	cout.precision(16);
	int N, M, L; cin >> N >> M >> L;
	vector<Runner> runners;
	REP(i, N) {
		Runner runner;
		double P; int T, V; cin >> P >> T >> V; P /= 100.0;
		double base_time = V > 0 ? (double)L / V : 1e20;
		REP(t_break, M + 1) {
			runner.goal_times.PB(base_time + T * t_break);
			runner.probabilities.PB(
				pow(P, t_break) * pow(1 - P, M - t_break) * combination(M, t_break) );
		}
		runners.PB(runner);
		//runner.inspect();
	}

	REP(i, runners.size()) {
		Runner &r = runners[i];
		double ans = 0;
		REP(t, M + 1) {
			double prize_prob = r.probabilities[t];
			REP(j, runners.size()) {
				if (i == j) continue;
				Runner &comp = runners[j];
				double win_prob = 0;
				REP(k, M+1) {
					if(comp.goal_times[k] > r.goal_times[t] + EPS) {
						win_prob += comp.probabilities[k];
					}
				}
				prize_prob *= win_prob;
			}
			ans += prize_prob;
		}
		printf("%.8f\n",ans);
		//cout << ans << endl;
	}
}

