int main(void){
	comb[0][0]=1;
	for(int i=1;i<=100;i++){
		comb[i][0]=1;
		comb[i][i]=1;
		for(int j=1;j<i;j++){
			comb[i][j]=comb[i-1][j]+comb[i-1][j-1];
		}
	}
	scanf("%d %d %lf",&n,&m,&l);
	for(int i=0;i<n;i++){
		scanf("%lf %d %lf",&p[i],&t[i],&v[i]);
		p[i]=p[i]/100.0;
	}
	for(int j=0;j<n;j++){
		for(int k=0;k<=m;k++){
			pp[j][k]=comb[m][k];
			for(int l=0;l<k;l++){
				pp[j][k]*=p[j];
			}
			for(int l=0;l<m-k;l++){
				pp[j][k]*=(1.0-p[j]);
			}
		}
	}
	for(int i=0;i<n;i++){
		for(int j=0;j<=m;j++){
			psum[i][j+1]=psum[i][j]+pp[i][j];
		}
	}
	for(int i=0;i<n;i++){
