	}
	for(int i=0;i<n;i++){
		if(v[i]==0.0)continue;
		for(int l=0;l<=m;l++){
			double wp=pp[i][l];
			for(int k=0;k<n;k++){
				if(k==i)continue;
				if(v[k]==0.0)continue;
				int uk=upper_bound(tt[k],tt[k]+m+2,tt[i][l])-tt[k];
