int Find(double x) {
        for (int i = 1; i <= tot; ++i)
		if (fabs(x - c[i]) < eps)
			return i;
}

double co[MAXN][MAXM];

inline int lowbit(int x) {
	return x & (-x);
}

inline void Add(int i, int x, double v) {
	if (x == 0)
		return;
	for (; x <= tot; x += lowbit(x))
		co[i][x] += v;
}

inline double Query(int i, int x) {
	double ret = 0;
	for (; x > 0; x -= lowbit(x))
		ret += co[i][x];
	return ret;
}
int main() {
	C[0][0] = 1;
	for (int i = 1; i <= 50; ++i) {
		C[i][0] = 1;
		for (int j = 1; j <= i; ++j)
			C[i][j] = C[i - 1][j - 1] + C[i - 1][j];
	}
	scanf("%d%d%d", &n, &m, &l);
	for (int i = 1; i <= n; ++i) {
		scanf("%lf%lf%lf", &p[i], &t[i], &v[i]);
