			b[++tot] = -(org[i] + j * t[i]);
		}
	sort(b + 1, b + 1 + tot);
	int tmp = 0;
	for (int i = 1; i <= tot; ++i) {
		if (i == 1 || fabs(b[i] - b[i - 1]) > eps) {
			c[++tmp] = b[i];
		}
	}
	tot = tmp;
	for (int i = 1; i <= n; ++i) {
		for (int j = 0; j <= m; ++j) {
			double curs = -(org[i] + j * t[i]);
			double curp = C[m][j];
		        for (int k = 1; k <= j; ++k)
				curp *= p[i];
			for (int k = 1; k <= m - j; ++k)
				curp *= (1 - p[i]);
			int cur = Find(curs);
			Add(i, cur, curp);
		}
	}
	for (int i = 1; i <= n; ++i) {
		double ans = 0;
		for (int j = 0; j <= m; ++j) {
			double curs = -(org[i] + j * t[i]);
			double curp = C[m][j];
			for (int k = 1; k <= j; ++k)
				curp *= p[i];
			for (int k = 1; k <= m - j; ++k)
				curp *= (1 - p[i]);
			//	cout << i << ' ' << j << ' ' << curp << endl;
			int cur = Find(curs);
			for (int k = 1; k <= n; ++k) if (i != k) {
					curp *= Query(k, cur - 1);
			}
			ans += curp;
		}
		printf("%.8f\n", ans);
	}

	return 0;
}

